package com.malas.appsr.malasapp.activities;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import com.malas.appsr.malasapp.R;import com.ms.square.android.expandabletextview.ExpandableTextView;import java.util.Objects;public class AboutUsActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_about_us);        Objects.requireNonNull(getSupportActionBar()).setTitle("About Us");        ExpandableTextView expTv = findViewById(R.id.expand_text_view);        expTv.setText(getResources().getString(R.string.abc));    }    @Override    public void onBackPressed() {        super.onBackPressed();        finish();    }}